import React, { Component } from 'react';
import Newsitems from './Newsitems'; // Component for individual news items
import Spinner from './Spinner'; // Spinner component for loading state
import PropTypes from 'prop-types'; // For prop validation

export default class News extends Component {
    // Default props for the News component
    static defaultProps = {
        country: 'us', // Default country for news
        pageSize: 9, // Default number of articles per page
        category: 'general', // Default category of news
    };

    // Type checking for props
    static propTypes = {
        country: PropTypes.string,
        pageSize: PropTypes.number,
        category: PropTypes.string,
    };

    // Initialize state variables
    constructor() {
        super();
        this.state = {
            articales: [], // Array to store fetched articles
            loading: false, // Boolean to indicate loading state
            page: 1, // Current page number
        };
    }

    // Function to fetch and update news data
    async updateNews() {
        try {
            // Use mock data for testing
            const mockData = {
                articles: [
                    {
                        source: { name: "Mock Source" },
                        author: "Mock Author",
                        title: "Mock Title",
                        description: "Mock description for testing...",
                        url: "https://example.com",
                        urlToImage: "https://via.placeholder.com/150",
                        publishedAt: new Date().toISOString(),
                    },
                ],
                totalResults: 1,
            };
    
            this.setState({
                articales: mockData.articles,
                totalArticales: mockData.totalResults,
                loading: false,
            });
        } catch (error) {
            console.error("Error loading mock data:", error);
        }
    }    

    // Lifecycle method to fetch news after component mounts
    async componentDidMount() {
        this.updateNews();
    }

    // Handle click event for the "Next Page" button
    handleNextClick = async () => {
        this.setState({ page: this.state.page + 1 }, this.updateNews);
    };

    // Handle click event for the "Previous Page" button
    handlePreviousClick = async () => {
        this.setState({ page: this.state.page - 1 }, this.updateNews);
    };

    // Render method to display the component
    render() {
        return (
            <div className="container my-3">
                <h1 className="text-center">Top News Headlines</h1>

                {/* Show spinner while loading */}
                {this.state.loading && <Spinner />}

                <div className="row">
                    {/* Display news articles only if not loading */}
                    {!this.state.loading &&
                        Array.isArray(this.state.articales) &&
                        this.state.articales.map((element) => {
                            return (
                                <div className="col-md-4" key={element.url}>
                                    {/* Render individual news items */}
                                    <Newsitems
                                        title={element.title ? element.title.slice(0, 40) : 'No Title'}
                                        description={element.description ? element.description.slice(0, 80) : 'No Description'}
                                        imageUrl={element.urlToImage}
                                        newsUrl={element.url}
                                        author={element.author}
                                        date={element.publishedAt}
                                        source={element.source.name}
                                    />
                                </div>
                            );
                        })}
                </div>

                {/* Pagination buttons */}
                <div className="container d-flex justify-content-between">
                    {/* Disable "Previous Page" button on the first page */}
                    <button
                        disabled={this.state.page <= 1}
                        type="button"
                        onClick={this.handlePreviousClick}
                        className="btn btn-dark my-2 mx-2"
                    >
                        &larr; Previous Page
                    </button>

                    {/* Disable "Next Page" button if there are no more pages */}
                    <button
                        type="button"
                        disabled={this.state.page + 1 > Math.ceil(this.state.totalArticales / this.props.pageSize)}
                        className="btn btn-dark my-2 mx-2"
                        onClick={this.handleNextClick}
                    >
                        Next Page &rarr;
                    </button>
                </div>
            </div>
        );
    }
}
