import React, { Component } from 'react'
import Newsitems from './Newsitems'

export default class News extends Component {

    constructor() {
        super();
        this.state = {
            articales: [],
            loading: false,
            page:1 
        }    
    }    

    async componentDidMount(){
    // it run after the render part means as the render portion finish running it will start running this portion
    let url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=062cfa709b6d45a68425747443409336";
    let data = await fetch(url);
    let parsedData = await data.json()
    // data.json() is use to convert data into json format
        
    this.setState({
        articales: parsedData.articles,
        loading: true
    })
    }

    handleNextClick = async () => {

    }
    // handleNextClick is a function which will be called when the next button is clicked
    // it will fetch the next page of articles from the API and update the state with the new

    handlePreviousClick = () => {

    }
    //

    render() {
    return (
        <div className='container my-3 '>
        <h1>Top News Headlines</h1>
            <div className="row">
                {this.state.articales.map((element) =>{
                    return <div className="col-md-4" key={element.url}>
                    <Newsitems title ={element.title?element.title.slice(0,40):" "} description = {element.description?element.description.slice(0, 80):" "} imageUrl={element.urlToImage} newsUrl={element.url} />
                </div>
                })}
            </div>
            <div className="container d-flex justify-content-between">
            <button type="button" onclick={handlePreviousClick} className="btn btn-dark my-2 mx-2">&larr; Previous Page</button>
            <button type="button" onclick={handleNextClick} className="btn btn-dark my-2 mx-2">Next Page &rarr;</button>
            </div>
        </div>        
    )
    }
}
