import React, { Component } from 'react'
import Newsitems from './Newsitems'
import Spinner from './Spinner';
import PropTypes from 'prop-types'


export default class News extends Component {

    static defaultProps = {
        country: "us",
        pageSize: 10,
    }

    static propTypes = {
        country: PropTypes.string,
        pageSize: PropTypes.number,
    }

    constructor() {
        super();
        this.state = {
            articales: [],
            loading: false,
            page:1,
        }    
    }    

    async componentDidMount(){
    // it run after the render part means as the render portion finish running it will start running this portion
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=062cfa709b6d45a68425747443409336&page=1&pageSize=${this.props.pageSize}`;
    this.setState({loading:true});
    let data = await fetch(url);
    let parsedData = await data.json()
    // data.json() is use to convert data into json format
        
    this.setState({
        articales: parsedData.articles,
        totalArticales: parsedData.totalResults,
        loading: false
    })
    }

    handleNextClick = async () => {
        console.log("Next");
        if (this.state.page + 1 <= Math.ceil(this.state.totalArticales/this.props.pageSize)){
            let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=062cfa709b6d45a68425747443409336&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
            this.setState({loading: true});
            let data = await fetch(url);
            let parsedData = await data.json()
            // data.json() is use to convert data into json format
            this.setState({
                page: this.state.page + 1,
                articales: parsedData.articles,
                loading: false
            })
        }
    }
    // handleNextClick is a function which will be called when the next button is clicked
    // it will fetch the next page of articles from the API and update the state with the new

    handlePreviousClick = async () => {
        console.log("previous");
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=062cfa709b6d45a68425747443409336&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parsedData = await data.json()
        // data.json() is use to convert data into json format
        this.setState({
            page: this.state.page - 1,
            articales: parsedData.articles,
            loading: false
        })
    }
    // handlePreviousClick is a function which will be called when the previous button is clicked
    // it will fetch the previous page of articles from the API and update the state with the new

    render() {
    return (
        <div className='container my-3 '>
        <h1 className='text-center'>Top News Headlines</h1>
        {this.state.loading && <Spinner />}
            <div className="row">
                {!this.state.loading && this.state.articales.map((element) => {
                    return <div className="col-md-4" key={element.url}>
                    <Newsitems title ={element.title?element.title.slice(0,40):" "} description = {element.description?element.description.slice(0, 80):" "} imageUrl={element.urlToImage} newsUrl={element.url} />
                </div>
                })}
            </div>
            <div className="container d-flex justify-content-between">
            <button disabled={this.state.page<=1} type="button" onClick={this.handlePreviousClick} className="btn btn-dark my-2 mx-2">&larr; Previous Page</button>
            <button type="button" disabled={this.state.page + 1 > Math.ceil(this.state.totalArticales/this.props.pageSize)} className="btn btn-dark my-2 mx-2" onClick={this.handleNextClick}>Next Page &rarr;</button>
            </div>
        </div>        
    )
    }
}
